CREATE DATABASE IF NOT EXISTS `sample-events` DEFAULT CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci;
USE `sample-events`;

create table if not exists events
(
    id            int auto_increment    primary key,
    title         varchar(255)                            not null,
    `desc`        text                                    not null,
    startdate     timestamp default current_timestamp()   not null on update current_timestamp(),
    enddate       timestamp default '0000-00-00 00:00:00' not null,
    max_attendees int                                     not null
);


create table if not exists event_attendees
(
    attendee_id   int auto_increment    primary key,
    event_id      int                                   not null,
    name          varchar(255)                          not null,
    attendee_time timestamp default current_timestamp() null
    );

